type Token struct {
	TokenInfo struct {
		LoginToken string `json:"login_token,omitempty"`
		AppToken   string `json:"app_token"`
		UserId     string `json:"user_id"`
	} `json:"token_info"`
	ErrorCode string `json:"error_code"`
}

type http_header map[string]string
type http_method string
type http_result struct {
	Result 				[]byte
	Location 			*url.URL
}



func Login(user,password string) (Token,error) {
	var token Token
	_url := "https://api-user.huami.com/registrations/+86" + user + "/tokens"


	data := url.Values{
		"client_id" : {"HuaMi"},
		"password" : {password},
		"redirect_uri" : {"https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html"},
		"token" : {"access"},
	}

	header := http_header{
		"Content-Type": "application/x-www-form-urlencoded;charset=UTF-8",
		"User-Agent": "MiFit/4.6.0 (iPhone; iOS 14.0.1; Scale/2.00)",
	}

	var method http_method = "POST"

	res,err := Curl(_url,strings.NewReader(data.Encode()),method,header)
	if err != nil {
		return token,errors.New("获取token失败")
	}
	access := res.Location.Query().Get("access")

	_url = "https://account.huami.com/v2/client/login"
	//登录
	data = url.Values{
		"app_name" : {"com.xiaomi.hm.health"},
		"app_version" : {"4.6.0"},
		"code" : {access},
		"country_code" : {"CN"},
		"device_id" : {"2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1"},
		"device_model" : {"phone"},
		"grant_type" : {"access_token"},
		"third_name" : {"huami_phone"},
	}
	res,err = Curl(_url,strings.NewReader(data.Encode()),method,header)
	if err != nil {
		return token,errors.New("获取登录信息失败")
	}


	err = json.Unmarshal(res.Result, &token)
	if err != nil {
		return token,errors.New("登录信息转换map失败")
	}
	return token,nil
}

